import React, { Component } from 'react'
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react';

const mapStyles = {
    width:"400px",
    height: '65%',
  };


export class MapContainer extends Component {

  displayBeach = (event) => {
    event.preventDefault()
    this.props.history.push('/favbeach')
  }

  displayMarkers = () => {
    let beachesArray = this.props.beaches
    // console.log(beachesArray)
    return beachesArray.map((beach, index) => {
      return <Marker 
      key={index} 
      id={index} 
      displayBeach={this.props.displayBeach}
      position={{
        lat: beach.lat,
        lng: beach.lng
                }}
      onClick={(event) => this.props.displayBeach(event, beach) }/>
    })
  }

  render() {
    let firstBeach; 
    if (this.props.beaches.length > 0){
      firstBeach = {
      lat:parseFloat(parseFloat(this.props.beaches[0].lat).toFixed(3)), lng:parseFloat(parseFloat(this.props.beaches[0].lng).toFixed(3))}
    }else{ firstBeach = {lat:37.8591,lng:-122.4853}}

      return (
          <Map
          id="map-showcase"
          style={mapStyles}
          google={this.props.google}
          zoom={11}
          initialCenter={
            firstBeach
            }
          >
            {this.displayMarkers()}
          </Map>
      )
  }
}

export default GoogleApiWrapper({
    apiKey: 'AIzaSyCrN9OLFD2SSP9_lYIqcGINzsOJ1iAJ7fM',

  })(MapContainer);
