import React, { Component } from 'react'


export default class SearchForm extends Component {

    state={city:"",
    state:""}

    handleChange = (event) => {
        event.preventDefault()
        this.setState({...this.state, [event.target.name]:event.target.value})
        }

    onSubmit = (event) => {
        event.preventDefault()
        let city = this.state.city
        let state = this.state.state
        fetch(`https://maps.googleapis.com/maps/api/geocode/json?address=${city},${state}&key=AIzaSyDhdrtDDMX9kJqDm0xrsIntQQhbIrdOueQ`
            ).then(res=>res.json())
            .then(respObj =>{   
                    this.props.queryCode(respObj)
                    })
                this.setState({city:"", state:""})
            }

    render() {
        return (
            <div >
               <form className="App" onSubmit={this.onSubmit}>
                           Please Enter Your Location:
                    <label>
                    <input  onChange={this.handleChange} 
                            type="text"
                            placeholder="city"
                            name="city" 
                            display="city"
                            defaultValue={this.state.city}
                            autoComplete="off">
                    </input>
                    </label>
                    <input  onChange={this.handleChange}
                            type="text"
                            name="state"
                            placeholder="state"
                            defaultValue={this.state.state}
                            autoComplete="off">        
                    </input>
                    <div></div>
                    <button id="returnbutton" type="submit" value="Submit">Submit</button>
                </form>
            </div>
        )
    }
}
