import React, { Component } from 'react'
import MapContainer from '../src/containers/mapcontainer'
import './index.css';
import SearchForm from '../src/components/searchform.js'
// import FavBeaches from '../src/components/favbeaches'
// import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
// import MainDisplay from './src/containers/main?display'
// import Map from './src/containers/map'
// import SearchBar from '../src/components/searchbar'
export default class App extends Component {
    state = {
        results:[],
        geocode:{lat:"",
                long:""}
    }

    grabBeaches = async (QlatLong) => {
        let rawBeaches = await fetch('http://localhost:3000/favbeaches')
        let beaches = await rawBeaches.json()
        let fbeach;
        if(fbeach.length>0){
        fbeach = beaches.filter(beach => {
            if(parseFloat(beach.lat) <= QlatLong[0]+.005 && 
            parseFloat(beach.lat) >= QlatLong[0]-.005 ){
            return beach}
        })
        this.setState({results:fbeach})
        }
    }

    queryCode = (responseObj) => {
        let QlatLong = []
        QlatLong.push(responseObj.results[0].geometry.location.lat, 
            responseObj.results[0].geometry.location.lng)
            console.log(QlatLong)
        this.grabBeaches(QlatLong)
    }


    render() {  
        return (
            <div>
                    <div id="search-bar">
                    <SearchForm grabBeaches={this.grabBeaches}
                                queryCode={this.queryCode}
                    />
                    </div>

                    <div id="map-showcase"> 
                    {/* <FavBeaches/>                 */}
                    <MapContainer beaches={this.state.results}/>
                    </div>
                    {/* <MuiThemeProvider>
                    <SearchBar/>
                    </MuiThemeProvider> */}
            </div>
        )
    }
}
