import React, { Component } from 'react'

export default class Reviews extends Component {

    state = {
        reviews:[]
      };  


    handleChange = (event) => {
        this.setState({ value:event.target.value})
    }


    render() {
        let reviewsArr = this.props.reviews 
        this.setState({reviews:reviewsArr})
        const allReviews = reviewsArr.map((review)=>{
          return <span key={review.id}>
          <button onClick={()=>this.props.deleteReview(review.id)}>Delete</button>
          <textarea value={review.review} onChange={this.handleChange}>
          {review.review} 
          </textarea>
          <button onClick={()=>this.props.updateReview(this.state.value)}>Update</button>
          <br></br></span>
        })

        return (
            <div>
            <div>Reviews of Beaches</div>
                {/* <button>All Beach Reviews</button> */}
                <div>
            {allReviews}
                </div>
            </div>
            )
    }
}
