            responseObj.results[0].geometry.location.lng)
            console.log(QlatLong)
        this.grabBeaches(QlatLong)
    }


    render() {  
        return (
            <div>
                    <div id="search-bar">
                    <SearchForm grabBeaches={this.grabBeaches}
                                queryCode={this.queryCode}
                    />
                    </div>

                    <div id="map-showcase"> 
                    {/* <FavBeaches/>                 */}
                    <MapContainer beaches={this.state.results}/>
                    </div>
                    {/* <MuiThemeProvider>
