import React, { Component } from 'react'
import MapContainer from '../src/containers/mapcontainer'
import './index.css';
import SearchForm from '../src/components/searchform.js'
import Favbeach from '../src/components/favbeach'
// import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
// import MainDisplay from './src/containers/main?display'
// import Map from './src/containers/map'
export default class App extends Component {
    state = {
        results:[],
        displayBeach:[],
        user: {
            id: 1
        },
        geocode:{lat:"",
                long:""}
    }

    grabBeaches = async (QlatLong) => {
        let rawBeaches = await fetch('http://localhost:3000/favbeaches')
        let beaches = await rawBeaches.json()
        let fbeach = beaches.filter(beach => {
            // if (beach.id === 347){
            //     console.log(`${parseFloat(beach.lat)} <= ${QlatLong.lat+.05}`)
            // }
            return parseFloat(beach.lat) <= QlatLong.lat+.05 && 
            parseFloat(beach.lat) >= QlatLong.lat-.05
        })
        let newBeaches = fbeach
        // console.log(fbeach)
        this.setState({results:newBeaches})
    }

    queryCode = (responseObj) => {
        let QlatLong = {}
        if (responseObj.results.length >0){
            QlatLong.lat = responseObj.results[0].geometry.location.lat
            QlatLong.lng =  responseObj.results[0].geometry.location.lng
        }else{
            QlatLong = {lat:37.8591,lng:122.4853}
        }
            // console.log("default", QlatLong)
        this.grabBeaches(QlatLong)
    }

    displayBeach = (clickedBeach) => {
        if(this.displayBeach.lenght > 0){
        console.log("clickedBeach",clickedBeach)
        this.setState({displayBeach:clickedBeach})}
    }


    render() {  
        return (
            <div>
                    <div id="search-bar">
                    <SearchForm grabBeaches={this.grabBeaches}
                                queryCode={this.queryCode}
                    />
                    </div>
                    <div id="fav-beach">
                    <Favbeach displayBeach={this.state.displayBeach}/>                
                    </div>
                    <div id="map-showcase"> 
                    <MapContainer beaches={this.state.results} displayBeach={this.displayBeach}/>
                    </div>
            </div>
        )
    }
}
