import React, { Component } from 'react'


export default class ReviewForm extends Component {

    state = {
        value: 'Please submit a beach review.'
      };  

    handleChange = (event) => {
        this.setState({...this.state, [event.target.name]:event.target.value})
    }

    handleSubmit = (event) => {
        alert('Thanks for Your Review: ' + this.state.value);
        event.preventDefault();
        let newBeachReview = this.state.value
        let favbeach_id = this.props.displayBeach.id 
        let user_id = 1
        let newBeachReviewObj = {
            review:newBeachReview,
            favbeach_id:favbeach_id,
            user_id:user_id 
        }
        const url = "http://localhost:3000/reviews"
        fetch(url, {method: 'POST',
        body:JSON.stringify(newBeachReviewObj),
        headers: {'content-type':'application/json'}
        }).then(res => res.json())
        .catch(error=>console.error("Error:", error))
        .then(res => {
            console.log('Success:', res)
        })
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                <label>
                    Review Text Here:
                    <textarea defaultValue={this.state.value} 
                    />
                </label>
                <input type="submit" value="Submit" onChange={this.handleChange}/>
                </form>
            </div>
        )
    }
}
