import React, { Component } from 'react'

export default class Reviews extends Component {

    state = {
        reviews:[],
        loaded: false
    };  

    handleChange = (event) => {
        this.setState({reviews:event.target.value})
    }

    setsometnig = () => {
     if (this.props.reviews) {
           this.setState({
              loaded: true
          })
    }
}

    reviewsLoaded = () => {
            let reviewsArr = this.props.reviews.sort((a,b) => b.id - a.id)
            // const myReviews = reviewsArr.filter(review=> review.user_id === this.props.user_id)
            // console.log("myreviews", myReviews)
            return reviewsArr.map((review)=>{
                if (review.user_id === this.props.user_id){
                    console.log("review.beach", review.beach)
                    return <span key={review.id}>
                    <div>Beach Name:    
                                       {review.beach.name}</div>
                  <div>Beach Id:
                    {review.beach_id}
                     </div>
                  <textarea defaultValue={review.review} onChange={this.handleChange}>
                  </textarea>
                  <button onClick={()=>this.props.deleteReview(review.id)}
                  className="btn btn-info btn-sm">Delete</button>
                  <button onClick={()=>this.props.updateReview(review, this.state.reviews)}
                  className="btn btn-warning btn-sm">Update</button>
                  <br></br></span>
                    }
                })
        }
        render() {

            return (
                <div className="container reviews" >
                <div>Reviews of Beaches</div>
                    {/* <button>All Beach Reviews</button> */}
                    <div>
                    {!this.props.reviews ?  null : this.setsometnig() }
                    {!this.state.loaded ?  console.log() : this.reviewsLoaded() }

                {/* {allReviews} */}
                    </div>
                </div>
                )
        }
    }




