import React, { Component } from 'react'
import {Pie} from 'react-chartjs-2';

// let ctx = document.getElementById('myChart').getContext('2d');
let labels = ["Dogs Friendly 🐕", "Stroller Friendly 👶🏻", "Fishing 🎣"]
let colorHex = ['#FB3640', '#EFCA08', '#43AA8B']
console.log("rawbeaches",this.props.rawBeaches)
const dogBeaches = this.props.rawBeaches.filter(beach => beach.dog_friendly === "yes")
let dbCount = (dogBeaches.length/1563)*100
console.log(dbCount)
const strollerBeaches = this.props.rawBeaches.filter(beach => beach.ez4strollers === "yes")
let sbCount = (strollerBeaches.length/1563)*100
const fishBeaches = this.props.rawBeaches.filter(beach => beach.fishing === "yes")
let fbCount = (fishBeaches.length/1563)*100

console.log(dbCount,sbCount, fbCount)

const data = {
    labels: labels,
        datasets: [{
            data: [30, 30, 30],
            backgroundColor: colorHex,
            hoverBackgroundColor: colorHex
        }],
};

export default class beachchart extends Component {
    render() {
        return (
            <div>
                <h2 id="piechart">Beach Data</h2>
                <Pie id="piechart" data={data}/>
            </div>
        );
    }
}
