import React, { Component } from 'react'


export default class searchform extends Component {

    state={city:"city",
    state:"state"}

    handleChange = (event) => {
        event.preventDefault()
        this.setState({...this.state, [event.target.name]:event.target.value})
        console.log("hello")
    }

    onSubmit = (event) => {
        event.preventDefault()
        let city = this.state.city
        let state = this.state.state
        let proxyUrl = 'https://git.heroku.com/mighty-wildwood-58234.git'
            targetUrl = `https://maps.googleapis.com/maps/api/place/textsearch/json?query=${city}+"${state}"+beaches&key=AIzaSyCrN9OLFD2SSP9_lYIqcGINzsOJ1iAJ7fM`


        fetch(`${url}`)
        .then(res => res.json())
        .then((resArray)=>{
        this.props.grabBeaches(resArray)})

        this.setState({city:"", 
                        state:""})
        }


    render() {
        return (
            <div >
               <form className="App" onSubmit={this.onSubmit}>
                           Please Enter Your Location:
                    <label>
                    <input  onChange={this.handleChange} 
                            type="text"
                            name="city" 
                            display="city"
                            defaultValue={this.state.city}
                            autoComplete="off">
                    </input>
                    </label>
                    <input  onChange={this.handleChange}
                            type="text"
                            name="state"
                            defaultValue={this.state.state}
                            autoComplete="off">        
                    </input>
                    <button type="submit" value="Submit"/>
                </form>
            </div>
        )
    }
}
