import React, { Component } from 'react'
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react';

const mapStyles = {
    width:"600px",
    height: '80%',
    float:"left",
    position:"none",
    transform:"translate(-235px,-780px)",
    color:"black",
    borderRadius:"80px",
    textShadow:"none"
  };


class MapContainer extends Component {

  displayMarkers = () => {
    let marker = require("../assets/backgrounds/beach-marker.png")
    let beachesArray = this.props.beaches
    return beachesArray.map((beach, index) => {
      return <Marker 
      icon={{url: marker,
        scaledSize: new window.google.maps.Size(40, 40)
      }}
      key={index} 
      id={index} 
      displayBeach={this.props.displayBeach}
      position={{
        lat: beach.lat,
        lng: beach.lng
                }}
      onClick={() => this.props.displayBeach(beach, this.props) }/>
    })
  }


  firstBeachAsync = async () =>{
    // let firstBeach; 
    let beachProps = await this.props.beaches[10]
    let firstBeach = {
      lat:parseFloat(parseFloat(beachProps.lat)), lng:parseFloat(parseFloat(beachProps.lng))}
      return firstBeach
  }


  render () {

    // let firstBeach; 

    // if (this.props.beaches>0){
    //   firstBeach = {
    //   lat:parseFloat(parseFloat(this.props.beaches[10].lat)), lng:parseFloat(parseFloat(this.props.beaches[10].lng))}
    // }else{ 
    //   firstBeach = {
    //     lat:37.8591,lng:-122.4853
    //   }}

      return (
        // firstBeach ? <div>Loading...</div>:
        <div className="map-container">
          <Map
          id="map-showcase"
          style={mapStyles}
          google={this.props.google}
          zoom={11}
          initialCenter={
            firstBeach
            }
          >
            {this.displayMarkers()}
          </Map>
          </div>


      )
  }
}
const API_KEY = process.env.REACT_APP_GOOGLE_MAPS_KEY
export default GoogleApiWrapper({
    apiKey:API_KEY,
  })(MapContainer);
