import React, { Component } from 'react'

export default class Reviews extends Component {
    render() {
        let reviewsObj = this.props.reviews 
        const allReviews = reviewsObj.map((review)=>{
          return <span key={review.id}>
          <button onClick={()=>this.props.deleteReview(review.id)}>Delete</button>
          <textarea >
          {review.review} 
          </textarea>
          <button onClick={()=>this.props.updateReview(review)}>Update</button>
          <br></br></span>
        })

        return (
            <div>
            <div>Reviews of Beaches</div>
                {/* <button>All Beach Reviews</button> */}
                <div>
            {allReviews}
                </div>
            </div>
            )
    }
}
