import React, { Component } from 'react'
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react';

const mapStyles = {
    width: '50%',
    height: '50%',
    borderColor: "red",
    border: "50px",
  };


export class MapContainer extends Component {
  displayMarkers = () => {
    let beachesArray = this.props.beaches
    return beachesArray.map((beach, index) => {
      return <Marker key={index} id={index} position={{
        lat: beach.lat,
        lng: beach.lng
      }}
      onClick={() => console.log("You clicked me!")} />
    })
  }

  render() {
    let firstBeach; 
    if (this.props.beaches.length > 0){
      firstBeach = {
      lat:this.props.beaches[0].lat, lng:this.props.beaches[0].lng}
    }else{return firstBeach = {lat:37.8591,lng:122.4853}}

      return (
          <Map
          id="map-showcase"
          style={mapStyles}
          google={this.props.google}
          zoom={11}
          initialCenter={{
            firstBeach
            }}
          >
            {this.displayMarkers()}
          </Map>
      )
  }
}

export default GoogleApiWrapper({
    apiKey: 'AIzaSyCrN9OLFD2SSP9_lYIqcGINzsOJ1iAJ7fM',

  })(MapContainer);
