import React, { Component } from 'react'

export default class Reviews extends Component {

    state = {
        reviews:[]
    };  

    handleChange = (event) => {
        this.setState({reviews:event.target.value})
    }

    render() {
        let allBeaches = this.props.beaches
        let beachName;
        // console.log("beaches", allBeaches)
        const reviewsArr = this.props.reviews 
        const allReviews = reviewsArr.map((review)=>{
            if (review.user_id == this.props.user_id){

                return <span key={review.id}>
          <div>Beach:{review.favbeach_id
             }</div>
          <textarea defaultValue={review.review} onChange={this.handleChange}>
          </textarea>
          <button onClick={()=>this.props.deleteReview(review.id)}
          className="btn btn-info btn-sm">Delete</button>
          <button onClick={()=>this.props.updateReview(review, this.state.reviews)}
          className="btn btn-warning btn-sm">Update</button>
          <br></br></span>
            }else{
                <span></span>
            }
        })

        return (
            <div className="container reviews" >
            <div>Reviews of Beaches</div>
                {/* <button>All Beach Reviews</button> */}
                <div>
            {allReviews}
                </div>
            </div>
            )
    }
}
