import React, { Component } from 'react'

export default class Reviews extends Component {

    state = {
        reviews:[]
    };  

    handleChange = (event) => {
        this.setState({reviews:event.target.value})
    }

    render() {
        const thisBeach = this.props.beaches.filter(beach=>{
            return beach.id === review.beach_id})
        const reviewsArr = this.props.reviews 
        const allReviews = reviewsArr.map((review)=>{
          return <span key={review.id}>
          <button onClick={()=>this.props.deleteReview(review.id)}>Delete</button>
          <div>Beach:{review.beach_id}</div>
          <textarea defaultValue={review.review} onChange={this.handleChange}>
          </textarea>
          <button onClick={()=>this.props.updateReview(review, this.state.reviews)}>Update</button>
          <br></br></span>
        })

        return (
            <div>
            <div>Reviews of Beaches</div>
                {/* <button>All Beach Reviews</button> */}
                <div>
            {allReviews}
                </div>
            </div>
            )
    }
}
